import React from "react";
import "./Intro.css";

import Typed from "react-typed";
import Particles from 'react-particles-js';

import BG from "../../video-bg.mp4";
import AD from "../../AD.jpg";

const Intro = () => {
  return (
    <div class="intro" id="intro">
        <Particles
        params={{
          particles: {
            number: {
              value: 50,
              density: {
                enable: true,
                value_area: 900,
              },
            },
            line_linked: {
                shadow: {
                    enable: true,
                    color: "#000",
                    blur: 100
                }
            },
            shape: {
              type: "triangle",
              stroke: {
                width: 6,
                color: "#f9ab00",
              },
            },
          },
        }}
      />
      <div class="left-intro">
        <img
          classname="ad"
          src={AD}
          alt="ad"
          style={{
            width: "150px",
            height: "150px",
            borderRadius: "75px",
            border: "3px solid orange",
          }}
        />
        <h1 class="ad-name">ankit dhawan</h1>
        <Typed
          class="typed-text"
          style={{ fontSize: ""}}
          strings={[
            "Android Development",
            "React Native",
            "Unity Game Development",
            "UI/UX",
            "Web Development",
          ]}
          typeSpeed={50}
          backSpeed={40}
          loop
        />
      </div>
      <video
      class="video-bg"
        autoPlay
        loop
        muted
        style={{ opacity: "0.2", width: "100%", zIndex: 0 }}
      >
        <source src={BG} type="video/mp4" />
      </video>
    </div>
  );
};

export default Intro;
