import React from "react";

import BG from "../../video-bg.mp4";
import AD from "../../AD.jpg";

const about = () => {
  return (
    <div className="page" style={{ width: "100%", height: "90vh" }}>
      <div
        style={{
          width: "50%",
          height: "100vh",
          position: "absolute",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          bottom: 80
        }}
      >
        <img
          src={AD}
          alt="ad"
          style={{
            width: "200px",
            height: "200px",
            borderRadius: "100px",
            border: "2px solid orange",
          }}
        />
      </div>
      <div style={{ backgroundColor: "#000", width: "100%" }}>
        <video autoPlay loop muted style={{ opacity: 0.3 }}>
          <source src={BG} type="video/mp4" />
        </video>
      </div>
    </div>
  );
};

export default about;
