import React, { useState, useEffect } from "react";
import Modal from "react-modal";

import "./Domains.scss";
import { apps, game, graphics } from "../data";

Modal.setAppElement("#root");

const Domains = ({ img, alt, title }) => {
  const [modalIsOpen, setIsModalOpen] = useState(false);
  const [selected, setSelected] = useState("apps");
  const [data, setData] = useState([]);

  useEffect(() => {
    switch (selected) {
      case "apps":
        setData(apps);
        break;
      case "game":
        setData(game);
        break;
      case "graphics":
        setData(graphics);
        break;
      default:
        setData(apps);
    }
  }, [selected]);
  return (
    <div class="main-item" onClick={() => setIsModalOpen(true)}>
      <Modal
        isOpen={modalIsOpen}
        shouldCloseOnOverlayClick={true}
        onRequestClose={() => setIsModalOpen(false)}
        style={{
          overlay: {
            backgroundColor: "black",
          },
          content: {
            backgroundColor: "#191e24",
            color: "white",
          },
        }}
      >
        <div class="modal-view">
          {projects.map((m) => (
            <div class="modal-item">
              <img src={m.img} alt="" />
              <h3>{m.title}</h3>
            </div>
          ))}
        </div>
      </Modal>
      <img class="img" src={img} alt={alt} />
      <h1>{title}</h1>
    </div>
  );
};

export default Domains;
