import React from "react";
import "./Intro.css";

import Typed from "react-typed";

import BG from "../../video-bg.mp4";
import AD from "../../AD.jpg";

const Intro = () => {
  return (
    <div class="intro" id="intro">
      <div class="left-introi">
        <img
          classname="ad"
          src={AD}
          alt="ad"
          style={{
            width: "150px",
            height: "150px",
            borderRadius: "75px",
            border: "3px solid orange",
          }}
        />
        <Typed
          class="typed-text"
          strings={[
            "Android Development",
            "React Native",
            "Unity Game Development",
            "UI/UX",
            "Web Development",
          ]}
          style={{ fontSize: "1.6rem", color: "#fff" }}
          typeSpeed={50}
          backSpeed={40}
          loop
        />
      </div>
      <video
        autoPlay
        loop
        muted
        style={{ opacity: "0.2", width: "100%", zIndex: 0 }}
      >
        <source src={BG} type="video/mp4" />
      </video>
    </div>
  );
};

export default Intro;
