import React, { useEffect, useState } from "react";
import "./Work.scss";
import PortfolioList from "../PortfolioList/PortfolioList";

import { skillsPortfolio, experiencePortfolio } from "../data";
import projectsTimeline from "./projectsTimeline";

const Work = () => {
  const [selected, setSelected] = useState("skills");
  const [data, setData] = useState(true);
  const list = [
    {
      id: "projects",
      title: "Projects",
    },
    {
      id: "experience",
      title: "Experience",
    },
  ];

  useEffect(() => {
    switch (selected) {
      case "projects":
        setData(projectsTimeline);
        break;
      case "experience":
        setData(experiencePortfolio);
        break;
      default:
        setData(skillsPortfolio);
    }
  }, [selected]);

  return (
    <div class="portfolio" id="portfolio">
      <h1>Works</h1>
      <projectsTimeline />
      <ul>
        {list.map((item) => (
          <PortfolioList
            title={item.title}
            active={selected === item.id}
            setSelected={setSelected}
            id={item.id}
          />
        ))}
      </ul>
      <div class="container">
        {/* {data.map((d) => (
          <div class="item">
            <img src={d.img} alt="" />
            <div class="title">
              <h3>{d.title}</h3>
            </div>
          </div>
        ))} */}
      </div>
    </div>
  );
};

export default Work;
