import React from "react";
import "./Intro.css";

import BG from "../../video-bg.mp4";
import AD from "../../AD.jpg";

const Intro = () => {
  return (
    <div class="intro" id="intro">
      <img
          classname="ad"
          src={AD}
          alt="ad"
          style={{
            width: "150px",
            height: "150px",
            borderRadius: "75px",
            border: "3px solid orange",
            position: "absolute",
            zIndex: "2",
          }}
        />
        <Particles
        params={{
          particles: {
            number: {
              value: 50,
              density: {
                enable: true,
                value_area: 800,
              },
            },
            shape: {
              type: "triangle",
              stroke: {
                width: 6,
                color: "#f9ab00",
              },
            },
          },
        }}
      />
      <video autoPlay loop muted style={{ opacity: "0.3", width: "100%", zIndex: 0 }}>
        <source src={BG} type="video/mp4" />
      </video>
    </div>
  );
};

export default Intro;
