import React, { useEffect, useState } from "react";
import "./Portfolio.scss";
import PortfolioList from "../PortfolioList/PortfolioList";

import Cheftastic from "../../Cheftastic.png";

const Portfolio = () => {
  const [selected, setSelected] = useState("featured");
  const list = [
    {
      id: "projects",
      title: "Projects",
    },
    {
      id: "skills",
      title: "Skills",
    },
    {
      id: "experience",
      title: "Experience",
    },
    {
      id: "designing",
      title: "Designing",
    },
  ];
  return (
    <div class="portfolio" id="portfolio">
      <h1>Portfolio</h1>
      <ul>
        {list.map((item) => (
          <PortfolioList
            title={item.title}
            active={selected === item.id}
            setSelected={setSelected}
          />
        ))}
      </ul>
      <div class="container">
        <div class="item">
          <img class="cheftastic" src={Cheftastic} alt="cheftastic" />
          <h3>Cheftastic</h3>
        </div>
        <div class="item">
          <img class="cheftastic" src={Cheftastic} alt="cheftastic" />
          <h3>Cheftastic</h3>
        </div>
        <div class="item">
          <img class="cheftastic" src={Cheftastic} alt="cheftastic" />
          <h3>Cheftastic</h3>
        </div>
        <div class="item">
          <img class="cheftastic" src={Cheftastic} alt="cheftastic" />
          <h3>Cheftastic</h3>
        </div>
      </div>
    </div>
  );
};

export default Portfolio;
